import {
    getIconSvgTemplates,
    getLocalizationService,
    getOneConfig,
    getPathPrefix,
    getToken,
} from 'webruntime/lightningConfigProvider';
import localizationService from '../localizationService/defaultLocalizationConfig.js';

jest.mock('@app/basePath', () => 'basePath', { virtual: true });
jest.mock('../localizationService/defaultLocalizationConfig.js', () => jest.fn());

describe('webruntime/lightningConfigProvider', () => {
    describe('getPathPrefix', () => {
        it('returns basePath', () => {
            expect(getPathPrefix()).toBe('basePath');
        });
    });
    describe('getLocalizationService', () => {
        it('returns localizationService', () => {
            expect(getLocalizationService()).toBe(localizationService);
        });
    });
    describe('getToken', () => {
        it('returns null', () => {
            expect(getToken()).toBeNull();
        });
    });
    describe('getOneConfig', () => {
        it('returns densitySetting', () => {
            expect(getOneConfig()).toEqual({
                densitySetting: '',
            });
        });
    });
    describe('getIconSvgTemplates', () => {
        it('returns null', () => {
            expect(getIconSvgTemplates()).toBeNull();
        });
    });
});
