function componentEnableHandler({ element }) {
    Object.assign(element.style, {
        'margin-bottom': '15px',
    });
}

function componentDisableHandler({ element }) {
    Object.assign(element.style, {
        'margin-bottom': '',
    });
}

export { componentDisableHandler, componentEnableHandler };
