import * as lightningConfigProvider from 'lightning/configProvider';
import * as webruntimeConfigProvider from 'webruntime/lightningConfigProvider';

jest.mock('@app/basePath', () => '', { virtual: true });

/**
 * Ensures that webruntime/lightningConfigProvider's API matches lightning/configProvider's API
 * since we override the latter with former in /packages/webruntime-framework/src/index.js
 */
describe('webruntime/lightningConfigProvider (integration)', () => {
    Object.keys(lightningConfigProvider).forEach(key => {
        it(`lightning/configProvider's ${key} exists in webruntime/lightningConfigProvider`, () => {
            expect(webruntimeConfigProvider[key] !== undefined).toBe(true);
        });
    });

    Object.keys(webruntimeConfigProvider).forEach(key => {
        it(`webruntime/lightningConfigProvider's ${key} exists in lightning/configProvider`, () => {
            expect(lightningConfigProvider[key] !== undefined).toBe(true);
        });
    });
});
