const consoleLog = jest.spyOn(global.console, 'log');
const consoleError = jest.spyOn(global.console, 'error');

describe('webruntime/logger', () => {
    const webruntimeLogger = require('webruntime/logger');
    beforeEach(() => {
        jest.resetAllMocks();
    });
    it('log', () => {
        webruntimeLogger.log('MsgA', 'MsgB');
        expect(consoleLog).toHaveBeenCalledWith('[webruntime]', 'MsgA', 'MsgB');
    });
    it('error', () => {
        webruntimeLogger.logError('ErrorMsgA', 'ErrorMsgB');
        expect(consoleError).toHaveBeenCalledWith('[webruntime]', 'ErrorMsgA', 'ErrorMsgB');
    });
});
