/**
 * Barebones Lightning Web Runtime Framework
 */
import { loader } from 'webruntime_loader/loader';

// Register compat modules
if (
    process.env.LWC_TARGET === 'es5' &&
    window.WebruntimeCompat &&
    window.WebruntimeCompat.modules
) {
    loader.defineModules(window.WebruntimeCompat.modules);
}

// Register framework modules
loader.defineModules({
    'webruntime_loader/loader': loader,
});

export const { define } = loader;
